declare enum SpeakerType {
  /**
   * Sent to all players, also used for death chat.
   */
  Announce,
  /**
   * Normal player chat.
   */
  Chat,
  /**
   * Item speak, like cell phones.
   */
  Item,
  /**
   * Eliminator blue chat.
   */
  Eliminator,
  /**
   * Only sent to admins.
   */
  Admin,
  /**
   * Red message chat.
   */
  Message,
}
